                    { lastMessage }
                </div>

                <div 
                    className='ce-chat-subtitle-text ce-chat-subtitle-date'
                    style={{ ...styles.messageText, ...{ textAlign: 'right', width: '25%' } }}
                >
                    { daySinceSent(chat.last_message.created) }
                </div>
            </div>
        </div>
    )
}

